<?php/** * 主题相关参数设置 * * @since 2020-8-30 * @version 1.0830 * @package functions.php * @author zitonglu/910109610@qq.com * @copyright rujue.cn */function limiwuwp_ad_theme_setting(){  if($_POST['limiwuwp_update_themeoptions']=='true') {limiwuwp_themeoptions_update();}  add_theme_page(__('网站设置','limiwuwp'),__('网站设置','limiwuwp'),'administrator','theme_setting','limiwuwp_theme_setting'); }   add_action('admin_menu','limiwuwp_ad_theme_setting');//设置关键词function limiwuwp_themeoptions_update(){  update_option('limiwuwp_homeKeyword',$_POST['homeKeyword']);  update_option('limiwuwp_homeDescription',$_POST['homeDescription']);  update_option('limiwuwp_top_JQ',stripslashes($_POST['top_JQ']));  update_option('limiwuwp_bottom_JQ',stripslashes($_POST['bottom_JQ']));  update_option('limiwuwp_share_day',$_POST['share_day']);  update_option('limiwuwp_home_cate',$_POST['home_cate']);  update_option('CDN_array_url',$_POST['CDN_array_url']);  if($_POST['bg_url']){    update_option('limiwuwp_bg_url',$_POST['bg_url']);  }else{    update_option('limiwuwp_bg_url',get_template_directory_uri().'/image/bg.png');  }}function limiwuwp_theme_setting(){ ?><div class="wrap">  <h1><?php _e('网站设置','limiwuwp')?></h1>  <hr>  <form method="POST" action="" style="padding-left:20px;">  <input type="hidden" name="limiwuwp_update_themeoptions" value="true" /><!-- hidden -->  <table class="form-table" role="presentation">    <tbody>      <tr>        <th scope="row"><label for="homeKeyword"><?php _e('首页关键词','limiwuwp')?></label></th>        <td>          <input name="homeKeyword" type="text" id="homeKeyword" value="<?php echo get_option('limiwuwp_homeKeyword'); ?>" class="regular-text">          <p class="description"><?php _e('多个词请使用英文半角逗号隔开','limiwuwp')?></p>        </td>      </tr><!-- 首页关键词 end -->      <tr>        <th scope="row"><label for="homeDescription"><?php _e('首页描述','limiwuwp')?></label></th>        <td>          <textarea type="text" name="homeDescription" cols="60" rows="3"><?php echo get_option('limiwuwp_homeDescription'); ?></textarea>        </td>      </tr><!-- 首页描述 end -->      <tr>        <th scope="row"><label for="top_JQ"><?php _e('顶部JS/JQ','limiwuwp')?></label></th>        <td>          <textarea name="top_JQ" cols="60" rows="4"><?php echo get_option('limiwuwp_top_JQ'); ?></textarea>          <p class="description"><?php _e('网站顶部的JS/JQ代码','limiwuwp')?></p>        </td>      </tr><!-- 顶部JS/JQ end -->      <tr>        <th scope="row"><label for="bottom_JQ"><?php _e('底部JS/JQ','limiwuwp')?></label></th>        <td>          <textarea name="bottom_JQ" cols="60" rows="4"><?php echo get_option('limiwuwp_bottom_JQ'); ?></textarea>          <p class="description"><?php _e('网站底部的JS/JQ代码','limiwuwp')?></p>        </td>      </tr><!-- 底部JS/JQ end -->      <tr>        <th scope="row"><label for="CDN_array_url">CDN<?php _e('加速网址','dianzai')?></label></th>        <td>          <textarea name="CDN_array_url" cols="100" rows="10"><?php echo get_option('CDN_array_url'); ?></textarea>          <p class="description"><?php _e('输入需要加速的JS后者css文件，一行一个加速网址，相应位置插入类似代码','dianzai')?>:</p>          <p><code>&lt;?php _echo_CDN_URL('bootstrap.min.js')?&gt;<br>&lt;?php _echo_CDN_URL('bootstrap.min.css','css')?&gt;</code></p>        </td>      </tr><!-- CDN加速网址 end -->      <tr>        <th scope="row"><?php _e('免费分享时效(天)','limiwuwp')?></th>        <td>          <input name="share_day" type="number" id="share_day" value="<?php echo get_option('limiwuwp_share_day'); ?>" class="regular-text" placeholder="<?php _e('天','limiwuwp')?>">        </td>      </tr><!-- share_day -->      <tr>        <th scope="row"><label for="bg_url"><?php _e('背板图片网址','limiwuwp')?></label></th>        <td>          <input name="bg_url" type="url" id="bg_url" value="<?php echo get_option('limiwuwp_bg_url'); ?>" class="regular-text">          <p class="description"><?php _e('网站背景的URL','limiwuwp')?></p>        </td>      </tr><!-- 背板图片网址 end -->      <tr>        <th scope="row"><?php _e('首页分类展示','limiwuwp')?></th>        <td>          <input name="home_cate" type="text" id="home_cate" value="<?php echo get_option('limiwuwp_home_cate'); ?>" class="regular-text">          <p><?php _e('输入展示在首页上的分类ID或别名','limiwuwp');?></p>        </td>      </tr><!-- home_cate -->    </tbody>  </table>    <p><input type="submit" name="submit" id="submit" class="button button-primary" value="<?php _e('提交修改','limiwuwp') ?>">    </p>  </form></div><?php }?>